import os
os.remove("answear.txt")
def string_composition (k,string):
    composition=list()
    for i in range(len(string)-k+1):
        composition.append(string[i:i+k])
    return composition

string=('')
k=100
f=open("answear.txt","a")
for kmer in string_composition(k,string) :
    f.write(kmer)
    f.write(' ')
f.close()
